import React, { Component } from 'react';

import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';


class ChangeConfiguration extends Component {
    state = {
        history: [{
            payload: undefined,
            timestamp: undefined
        }]
    }

    useStyles = makeStyles({
        table: {
          minWidth: 650,
        },
      });

    renderTable() {
        return this.state.history.map((element, index) => {
            const { payload, timestamp } = element
            return (
                <tr key={index}>
                    <td>{index}</td>
                    <td>{payload}</td>
                    <td>{timestamp}</td>
                </tr>
            );
        })
    }

    loadData() {
        const apiCall = axios.get(`http://localhost:8080/history`).then(response => {
            console.log(response);

            // this.setState({
            //     history: response.data.history
            // })
        });
    }

    render() {
        const classes = this.useStyles();
        this.loadData();
        return (
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="simple table">
                    <TableHead>
                        <TableRow>
                            <TableCell>#</TableCell>
                            <TableCell align="right">Payload</TableCell>
                            <TableCell align="right">Timestamp</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {this.state.history.map((row, index) => (
                            <TableRow key={index}>
                                <TableCell component="th" scope="row">
                                    {index}
                                </TableCell>
                                <TableCell align="right">{row.payload}</TableCell>
                                <TableCell align="right">{row.timestamp}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
            // <Table striped bordered hover>
            //     <thead>
            //         <tr>
            //             <th>#</th>
            //             <th>Payload</th>
            //             <th>Timestamp</th>
            //         </tr>
            //     </thead>
            //     <tbody>
            //         {this.renderTable()}
            //     </tbody>
            // </Table>
        );
    }
}

export default ChangeConfiguration;
