import React, { Component } from 'react';

import axios from 'axios';

import Table from 'react-bootstrap/Table'


class ChangeConfiguration extends Component {
    state = {
        history: [{
            payload: undefined,
            timestamp: undefined
        }]
    }

    renderTable() {
        return this.state.history.map((element, index) => {
            const { payload, timestamp } = element
            return (
                <tr key={index}>
                    <td>{index}</td>
                    <td>{payload}</td>
                    <td>{timestamp}</td>
                </tr>
            );
        })
    }

    loadData() {
        const apiCall = axios.get(`http://localhost:8080/history`).then(response => {
            console.log(response);
            
            // this.setState({
            //     history: response.data.history
            // })
          });
    }

    render() {
        this.loadData();
        return (
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Payload</th>
                        <th>Timestamp</th>
                    </tr>
                </thead>
                <tbody>
                    {this.renderTable()}
                </tbody>
            </Table>
        );
    }
}

export default ChangeConfiguration;
