import React from 'react';

const Form = (props) => (
    <form onSubmit={props.sendData}>
        <div>

            <div className="card">
                <div>Moisture level</div>
                <Slider
                    defaultValue={30}
                    aria-labelledby="discrete-slider"
                    valueLabelDisplay="auto"
                    step={10}
                    marks
                    min={10}
                    max={110}
                    
                />
            </div>
        </div>
        <button >Get Weather</button>
    </form>
);

export default Form;
